import React from 'react'
import Card from '../UI/Card';
import MealItem from './MealItem/MealItem';
import './AvailableMeals.css'


const AvailableMeals = async() => {

  const response = await fetch('https://console.firebase.google.com/u/0/project/react-http-1ca3c/database/react-http-1ca3c-default-rtdb/data/~2F');
  const data = await response.json();
  console.log(data);

  const  mealsArray = [];

  for (const key in data) {
    mealsArray.push({
      id:key,
      name: data[key].name,
      price: data[key].price,
      description: data[key].description
    })
  }

    const mealsList = mealsArray.map((meal) =>{
        return <MealItem id = {meal.id} key = {meal.id} name ={meal.name} description = {meal.description} price = {meal.price}/>
    })

  return (
    <div className='meals'>
        <Card>
        <ul>
            {mealsList}
        </ul>
        </Card>
    </div>
  )
}

export default AvailableMeals
